<div class="form">    <?php $form = $this->beginWidget('CActiveForm', array(        'id' => 'login-form',        'enableClientValidation' => true,        'clientOptions' => array(            'validateOnSubmit' => true,        ),    )); ?>    <?php echo $form->errorSummary($model); ?>    <div id="login_box" class="register_cont">        <h3>Already registered? Please Login Here</h3>        <p class="note">Fields with <span class="required">*</span> are required.</p>        <div class="reg_row">            <div class="left_sec"><?php echo $form->labelEx($model, 'username'); ?></div>            <div                class="right_sec"><?php echo $form->textField($model, 'username', array('id' => 'sell_email', 'style' => 'width: 222px;')); ?></div>        </div>        <div class="reg_row">            <div class="left_sec"><?php echo $form->labelEx($model, 'password'); ?></div>            <div                class="right_sec"><?php echo $form->passwordField($model, 'password', array('id' => 'sell_pwd')); ?></div>        </div>        <div class="reg_row">            <div class="left_sec">&nbsp;</div>            <div class="right_sec">                <?php echo CHtml::imageButton(Yii::app()->baseUrl . '/images/login_btn.gif', array('class' => 'mlr10', 'name' => 'login')); ?>                <a href='<?php echo $this->createUrl('realEstate/forgotPassword'); ?>'><?php echo CHtml::button('', array('class' => 'mlr10 forgot-password')); ?></a>            </div>        </div>    </div>    <div class="reg_row">        <div class="left_sec">New User</div>        <div class="right_sec">            <a href='<?php echo $this->createUrl('realEstate/search'); ?>'><?php echo CHtml::button('', array('class' => 'mlr10 image-buy')); ?></a>            <a href='<?php echo $this->createUrl('realEstate/sellerRegistration'); ?>'><?php echo CHtml::button('', array('class' => 'mlr10 image-sell')); ?></a>        </div>    </div>    <?php $this->endWidget(); ?></div><!-- form -->